package zhy2002.leetcode.additional;

import org.junit.Test;
import zhy2002.leetcode.additional.sumofproduct.*;

import java.math.BigInteger;
import java.util.Random;

import static org.hamcrest.MatcherAssert.assertThat;
import static org.hamcrest.Matchers.*;


public class SumOfProductTests {

    @Test
    public void naiveTest() {
        basicTest(new NaiveSolution());
    }

    @Test
    public void factTest() {
        basicTest(new FactSolution());
    }

    @Test
    public void formulaTest() {
        basicTest(new FormulaSolution());
    }

    @Test
    public void inductionTest() {
        basicTest(new InductionSolution());
    }

    @Test
    public void randomComparisonTest() {
        NaiveSolution solution1 = new NaiveSolution();
        FactSolution solution2 = new FactSolution();
        FormulaSolution solution3 = new FormulaSolution();
        InductionSolution solution4 = new InductionSolution();
        Random rand = new Random(5);
        for (int i = 0; i < 100; i++) {
            int n = rand.nextInt(100) + 1;
            int c = rand.nextInt(n + 5) + 1;
            BigInteger result = solution1.calculate(n, c);
            assertThat(result, equalTo(solution2.calculate(n, c)));
            assertThat(result, equalTo(solution3.calculate(n, c)));
            assertThat(result, equalTo(solution4.calculate(n, c)));
        }
    }

    private void basicTest(Solution solution) {
        BigInteger result = solution.calculate(1, 1);
        assertThat(result, equalTo(BigInteger.ZERO));

        result = solution.calculate(2, 1);
        assertThat(result, equalTo(BigInteger.ONE));

        result = solution.calculate(2, 1);
        assertThat(result, equalTo(BigInteger.ONE));

        result = solution.calculate(5, 2);
        assertThat(result, equalTo(new BigInteger("21")));

        result = solution.calculate(4, 3);
        assertThat(result, equalTo(new BigInteger("9")));

        result = solution.calculate(5, 3);
        assertThat(result, equalTo(new BigInteger("33")));

        result = solution.calculate(6, 3);
        assertThat(result, equalTo(new BigInteger("93")));

        result = solution.calculate(7, 4);
        assertThat(result, equalTo(new BigInteger("513")));

        result = solution.calculate(100, 10);
        assertThat(result, equalTo(new BigInteger("513946235090696089113")));

        if(!(solution instanceof FormulaSolution)) { //formula solution is to slow to solve these two cases.
            result = solution.calculate(1000000, 200);
            assertThat(result, equalTo(new BigInteger("")));

            result = solution.calculate(10000000, 200);
            assertThat(result, equalTo(new BigInteger("")));
        }
    }

    @Test
    public void miscTest() {
        InductionSolution solution = new InductionSolution();
        System.out.println(solution.calculate(100, 10));
        System.out.println(solution.calculate(1_000_000, 200));
        System.out.println(solution.calculate(10_000_000, 200));
    }
}
