package zhy2002.leetcode.tests;

import zhy2002.leetcode.solutions.longestpalindromicsubstring.DpSolution;
import zhy2002.leetcode.solutions.longestpalindromicsubstring.ExpansionSolution;
import zhy2002.leetcode.solutions.longestpalindromicsubstring.Solution;
import org.junit.Test;
import static org.junit.Assert.*;

public class LongestPalindromicSubstringTests {


    @Test
    public void expansionBasicTest(){
        basicTest(new ExpansionSolution());
    }

    @Test
    public void dpBasicTest(){
        basicTest(new DpSolution());
    }

    private void basicTest(Solution solution){
        assertEquals("",solution.longestPalindrome(""));
        assertEquals("a",solution.longestPalindrome("a"));
        assertEquals("aa",solution.longestPalindrome("aa"));
        assertEquals("aba",solution.longestPalindrome("abac"));
        assertEquals("cabbac",solution.longestPalindrome("cabbac"));
        assertEquals("abba",solution.longestPalindrome("cabba"));
        assertEquals("abba",solution.longestPalindrome("ecabbad"));
        assertEquals("abbaabba",solution.longestPalindrome("abbabbaabba"));
        assertEquals("jtdtj",solution.longestPalindrome(""));
        assertEquals("",solution.longestPalindrome(""));
        assertEquals("tattarrattat",solution.longestPalindrome("tattarrattat"));
        assertEquals("aba",solution.longestPalindrome("caba"));
        assertEquals("abba",solution.longestPalindrome("cbcaceccabbaaerewrew"));
    }
}
